import React, { useState } from "react";
import { StyleSheet, Text, Alert, Pressable, View } from "react-native";

import { ScrollView } from "react-native-gesture-handler";
import SafeAreaView from "react-native-safe-area-context";

import dbQuerySnapshot from "./services/dbQuerySnapshot";

export default function App() {
  const twoOptionAlert = () => {
    Alert.alert(
      //title
      produtos.name,
      //Body
      produtos.value,
      [
        {
          text: "yes",
          onPress: () => {
            console.log("Yes presionado");
          },
        },
        {
          text: "no",
          onPress: () => {
            console.log("No presionado");
          },
        },
      ]
    );
  };

  return (
    /*<SafeAreaView>*/
    <ScrollView style={styles.container}>
      <View style={styles.list}>
        {dbQuerySnapshot().map((produtos) => {
          return (
            <View
              style={styles.card}
              key={produtos.id}
              onPress={() => twoOptionAlert()}
            >
              <View style={styles.text}>
                <Text>{produtos.name}</Text>
                <Text>{"R$ " + produtos.value + ",00"}</Text>
              </View>
            </View>
          );
        })}
        {/*<View style={styles.textcss}>ok</View>*/}
      </View>
    </ScrollView>

    /*</SafeAreaView>*/
  );
}

const styles = StyleSheet.create({
  container: {
    flexDirection: "column",
  },
  list: {
    justifyContent: "center",
    flexDirection: "row",
    flexWrap: "wrap",
  },
  card: {
    margin: 3,
    padding: 10,
    minHeight: 75,
    textAlign: "center",
    alignItems: "center",
    justifyContent: "space-between",

    backgroundColor: "#1212",
  },
  text: {
    flexDirection: "column",
  },

  textcss: {
    margin: 5,
    padding: 10,
    minHeight: 75,
    minWidth: "30%",

    textAlign: "center",
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#F194FF",
  },
});
