import React, { useState } from "react";
import { StyleSheet, Text, Alert, Pressable, View } from "react-native";

import { ScrollView } from "react-native-gesture-handler";
import SafeAreaView from "react-native-safe-area-context";

import dbQuerySnapshot from "./services/dbQuerySnapshot";

export default function App() {
  return (
    /*<SafeAreaView>*/
    <ScrollView style={styles.container}>
      <View style={styles.list}>
        {dbQuerySnapshot().map((produtos) => {
          return (
            <View
              style={styles.card}
              key={produtos.id}
              onPress={() => Alert.alert("Modal has been closed.")}
            >
              <View style={styles.text}>
                <Text>{produtos.name}</Text>
                <Text>{"R$ " + produtos.value + ",00"}</Text>
              </View>
            </View>
          );
        })}
        <View style={styles.textcss}>ok</View>
      </View>
    </ScrollView>

    /*</SafeAreaView>*/
  );
}

const styles = StyleSheet.create({
  container: {
    flexDirection: "column",
  },
  list: {
    justifyContent: "space-between",
    flexDirection: "row",
    flexWrap: "wrap",
  },
  card: {
    margin: 5,
    padding: 10,
    minHeight: 75,
    maxWidth: "30%",
    marginVertical: 5,
    textAlign: "center",
    alignItems: "center",
    backgroundColor: "#1212",
    justifyContent: "center",
  },
  text: {
    flexDirection: "column",
  },

  textcss: {
    margin: 5,
    padding: 10,
    minHeight: 75,
    maxWidth: "30%",

    textAlign: "center",
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#F194FF",
  },
});
