import React, { useState } from "react";
import { StyleSheet, Text, Alert, Pressable, View } from "react-native";

import { ScrollView } from "react-native-gesture-handler";
import SafeAreaView from "react-native-safe-area-context";

import ModalView from "./components/modalPrudcts";

import dbQuerySnapshot from "./services/dbQuerySnapshot";

export default function App() {
  return (
    /*<SafeAreaView>*/
    <ScrollView style={styles.container}>
      <View style={styles.centeredView}>
        <Pressable
          style={[styles.button, styles.buttonOpen]}
          onPress={() => (
            <View>
              <ModalView />
            </View>
          )}
        >
          <Text style={styles.textStyle}>Show Modal</Text>
        </Pressable>
      </View>

      <View style={styles.list}>
        {dbQuerySnapshot().map((produtos) => {
          return (
            <View style={styles.card} key={produtos.id} onPress={() => {}}>
              <View style={styles.text}>
                <Text>{produtos.name}</Text>
                <Text>{"R$ " + produtos.value + ",00"}</Text>
              </View>
            </View>
          );
        })}
      </View>
    </ScrollView>

    /*</SafeAreaView>*/
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  list: {
    flex: 1,
    textAlign: "center",
  },
  card: {
    flex: 1,

    backgroundColor: "#1212",
    textAlign: "center",
    paddingVertical: 10,
    marginVertical: 10,
  },
  text: {
    flexDirection: "row",
    justifyContent: "space-between",
    marginHorizontal: 25,

    textAlign: "center",
  },
  centeredView: {
    justifyContent: "center",
    alignItems: "center",
    marginTop: 22,
  },
  modalView: {
    margin: 20,
    backgroundColor: "white",
    borderRadius: 20,
    padding: 35,
    alignItems: "center",
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 4,
    elevation: 5,
  },
  button: {
    borderRadius: 20,
    padding: 10,
    elevation: 2,
  },
  buttonOpen: {
    backgroundColor: "#F194FF",
  },
  buttonClose: {
    backgroundColor: "#2196F3",
  },
  textStyle: {
    color: "white",
    fontWeight: "bold",
    textAlign: "center",
  },
  modalText: {
    marginBottom: 15,
    textAlign: "center",
  },
});
