import React, { useState } from "react";
import { Button, StyleSheet, Text, Alert, View } from "react-native";

import { ScrollView } from "react-native-gesture-handler";
import SafeAreaView from "react-native-safe-area-context";

import dbQuerySnapshot from "./services/dbQuerySnapshot";

export default function App() {
  const twoOptionAlert = () => {
    alert("title", "title");
  };

  return (
    /*<SafeAreaView>*/
    <ScrollView style={styles.container}>
      <View style={styles.list}>
        {dbQuerySnapshot().map((produtos) => {
          return (
            <View style={styles.card} key={produtos.id}>
              <View style={styles.text}>
                <Text>{produtos.name}</Text>
                <Text>{"R$ " + produtos.value + ",00"}</Text>
              </View>
              <Button onPress={() => twoOptionAlert()}> + </Button>
            </View>
          );
        })}
        {/*<View style={styles.textcss}>ok</View>*/}
      </View>
    </ScrollView>

    /*</SafeAreaView>*/
  );
}

const styles = StyleSheet.create({
  container: {
    flexDirection: "column",
  },
  list: {
    justifyContent: "center",
    flexDirection: "row",
    flexWrap: "wrap",
  },
  card: {
    margin: 3,
    padding: 10,
    minHeight: 75,
    textAlign: "center",
    alignItems: "center",
    justifyContent: "space-between",

    backgroundColor: "#1212",
  },
  text: {
    flexDirection: "column",
  },

  textcss: {
    margin: 5,
    padding: 10,
    minHeight: 75,
    minWidth: "30%",

    textAlign: "center",
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#F194FF",
  },
});
